import React, { useState, useEffect } from 'react';
import './RubiksCube.css';
import { getCubeState, rotateFace, resetCube, scrambleCube, executeAlgorithm } from '../services/api';

const RubiksCube = () => {
    const [cubeState, setCubeState] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [algorithm, setAlgorithm] = useState('');
    const [scrambleMoves, setScrambleMoves] = useState(20);

    useEffect(() => {
        fetchCubeState();
    }, []);

    const fetchCubeState = async () => {
        try {
            setLoading(true);
            const data = await getCubeState();
            setCubeState(data);
            setError(null);
        } catch (err) {
            console.error('Error fetching cube state:', err);
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleRotate = async (face, clockwise) => {
        try {
            setLoading(true);
            const response = await rotateFace(face, clockwise);
            setCubeState(response.cube);
        } catch (error) {
            console.error('Error rotating face:', error);
            setError(`Failed to rotate face: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    const handleReset = async () => {
        try {
            setLoading(true);
            const response = await resetCube();
            setCubeState(response.cube);
        } catch (error) {
            console.error('Error resetting cube:', error);
            setError(`Failed to reset cube: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    const handleScramble = async () => {
        try {
            setLoading(true);
            const response = await scrambleCube(scrambleMoves);
            setCubeState(response.cube);
        } catch (error) {
            console.error('Error scrambling cube:', error);
            setError(`Failed to scramble cube: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    const handleAlgorithm = async () => {
        if (!algorithm.trim()) {
            setError('Please enter an algorithm');
            return;
        }

        try {
            setLoading(true);
            const response = await executeAlgorithm(algorithm);
            setCubeState(response.cube);
        } catch (error) {
            console.error('Error executing algorithm:', error);
            setError(`Failed to execute algorithm: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    // Render a single face of the cube
    const renderFace = (face, label, position) => {
        if (!cubeState || !cubeState[face]) {
            return null;
        }

        return (
            <div className={`face-container ${position}`}>
                <div className="face-label">{label}</div>
                <div className="face">
                    {[0, 1, 2].map(row => (
                        [0, 1, 2].map(col => (
                            <div
                                key={`${row}-${col}`}
                                className={`square ${cubeState[face][row][col]}`}
                            />
                        ))
                    ))}
                </div>
            </div>
        );
    };

    // Render buttons for rotating a face
    const renderFaceButtons = (face, label) => (
        <div className="face-controls">
            <span className="face-label">{label}</span>
            <button
                onClick={() => handleRotate(face, true)}
                disabled={loading}
                className="rotate-btn"
            >
                {label} ↻
            </button>
            <button
                onClick={() => handleRotate(face, false)}
                disabled={loading}
                className="rotate-btn"
            >
                {label} ↺
            </button>
        </div>
    );

    return (
        <div className="rubiks-cube-container">
            <h1>Rubik's Cube Simulator</h1>

            {error && (
                <div className="error-message">
                    <p>{error}</p>
                </div>
            )}

            {loading && <div className="loading-indicator">Loading...</div>}

            {cubeState && (
                <div className="cube-net">
                    {renderFace('up', 'U', 'up')}
                    {renderFace('left', 'L', 'left')}
                    {renderFace('front', 'F', 'front')}
                    {renderFace('right', 'R', 'right')}
                    {renderFace('back', 'B', 'back')}
                    {renderFace('down', 'D', 'down')}
                </div>
            )}

            <div className="controls-container">
                <div className="control-section">
                    <h3>Rotate Faces</h3>
                    <div className="face-buttons">
                        {renderFaceButtons('front', 'F')}
                        {renderFaceButtons('back', 'B')}
                        {renderFaceButtons('up', 'U')}
                        {renderFaceButtons('down', 'D')}
                        {renderFaceButtons('left', 'L')}
                        {renderFaceButtons('right', 'R')}
                    </div>
                </div>

                <div className="control-section">
                    <h3>Cube Operations</h3>
                    <div className="operation-buttons">
                        <button
                            onClick={handleReset}
                            disabled={loading}
                            className="operation-btn reset-btn"
                        >
                            Reset Cube
                        </button>

                        <div className="scramble-controls">
                            <button
                                onClick={handleScramble}
                                disabled={loading}
                                className="operation-btn scramble-btn"
                            >
                                Scramble
                            </button>
                            <input
                                type="number"
                                min="1"
                                max="100"
                                value={scrambleMoves}
                                onChange={e => setScrambleMoves(parseInt(e.target.value) || 20)}
                                className="scramble-moves"
                            />
                            <span>moves</span>
                        </div>
                    </div>
                </div>

                <div className="control-section">
                    <h3>Algorithm</h3>
                    <div className="algorithm-controls">
                        <input
                            type="text"
                            placeholder="e.g. R U R' U'"
                            value={algorithm}
                            onChange={e => setAlgorithm(e.target.value)}
                            className="algorithm-input"
                        />
                        <button
                            onClick={handleAlgorithm}
                            disabled={loading}
                            className="operation-btn algorithm-btn"
                        >
                            Execute
                        </button>
                    </div>
                    <div className="algorithm-help">
                        <small>
                            Valid moves: F, B, U, D, L, R, F', B', U', D', L', R', F2, B2, U2, D2, L2, R2
                        </small>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default RubiksCube;
